import {
    Outlet,
    Link,
    useLoaderData,
    Form
} from "react-router-dom";
import { getContacts, createContact } from "../contacts";
import {useState} from "react";
import {setUserAuthenticated, setAdminAuthenticated} from "../redux/auth";


export async function action() {
    const contact = await createContact();
    return { contact };
}

export async function loader() {
    const contacts = await getContacts();
    return { contacts };
}

export default function Root() {
    const { contacts } = useLoaderData();
    const [logged, setLogged] = useState()
    // const [authenticatedUser, setauthenticatedUser] = useState(false)
    // const [authenticatedAdmin, setauthenticatedAdmin] = useState(false)

    return (
        <>
            <nav>
                <ul>
                    {authenticatedUser &&
                        <>
                            <li>
                                <Link to={`/profile`}> Profile </Link>
                            </li>
                            <li>
                                <Link to={`/userdashboard`}> User Dashboard </Link>
                            </li>
                            <h1>Welcome User!!!</h1>
                        </>
                    }
                    {authenticatedAdmin &&
                        <>
                            <li>
                                <Link to={`/usersList`}> Users List </Link>
                            </li>
                            <li>
                                <Link to={`/admindashboard`}> Admin Dashboard </Link>
                            </li>
                            <h1>Welcome Admin!!!</h1>
                        </>
                    }
                </ul>
            </nav>
            {!logged && (<section>
                Welcome to the homepage (Root) !!!
                <br/>
                <button onClick={() => {
                    setLogged(true)
                    setAdminAuthenticated(true)
                }}>Click Here to Login as Admin !</button>
                <button onClick={() => {
                    setLogged(true)
                    setUserAuthenticated(true)
                }}>Click Here to Login as User !</button>
            </section>)
            }
            <div id="detail">
            <Outlet/>
            </div>
        </>
    );
}
