import { useEffect } from "react";
import { useFieldArray, useForm } from "react-hook-form";
import { DevTool } from "@hookform/devtools";

let renderCount = 0;

export default function Form() {
    const { register, control, handleSubmit, formState, watch, setValue, reset, trigger } = useForm({
        defaultValues: async () => {
            const response = await fetch(
                "https://jsonplaceholder.typicode.com/users/1"
            );
            const data = await response.json();
            return {
                username: "Batman",
                email: data.email,
                channel: "",
                address: {
                    line1: "",
                    line2: "",
                },
                age: 0,
                dob: new Date(),
                phone: [{ number: "" }],
            };
        },
        mode: "onTouched",
    });

    const { fields, append, remove } = useFieldArray({
        name: "phone",
        control,
    });

    const { errors, isDirty, touchedFields, isValid } = formState;

    console.log({ errors, isDirty, touchedFields, isValid });

    const onSubmit = (data) => {
        console.log("Form submitted", data);
    };

    const onError = (errors) => {
        console.log("Form errors", errors);
    };

    const onReset = () => {
        reset();
    };

    const handleGetValues = () => {
        console.log("Get values", watch("username"));
    };

    const handleSetValue = () => {
        setValue("username", "", {
            shouldValidate: true,
            shouldDirty: true,
            shouldTouch: true,
        });
    };

    useEffect(() => {
        const subscription = watch((value, { name, type }) =>
            console.log(value, name, type)
        );
        return () => subscription.unsubscribe();
    }, [watch]);

    useEffect(() => {
        if (isSubmitSuccessful) {
            reset();
        }
    }, [isSubmitSuccessful, reset]);

    renderCount++;
    return (
        <div>
            <h1>YouTube Form ({renderCount / 2})</h1>
            <form onSubmit={handleSubmit(onSubmit, onError)}>
                <div className="form-control">
                    <label htmlFor="username">Username</label>
                    <input
                        type="text"
                        id="username"
                        {...register("username", {
                            required: { value: true, message: "Username is required" },
                        })}
                    />
                    <p className="error">{errors.username?.message}</p>
                </div>

                {/* Rest of the form controls */}

                <div>
                    <label>List of phone numbers</label>
                    <div>
                        {fields.map((field, index) => (
                            <div className="form-control" key={field.id}>
                                <input
                                    type="text"
                                    {...register(`phone.${index}.number`)}
                                />

                                {index > 0 && (
                                    <button type="button" onClick={() => remove(index)}>
                                        Remove
                                    </button>
                                )}
                            </div>
                        ))}
                        <button
                            type="button"
                            onClick={() =>
                                append({
                                    number: "",
                                })
                            }
                        >
                            Add phone number
                        </button>
                    </div>
                </div>

                {/* Buttons for actions */}

            </form>
            <DevTool control={control} />
        </div>
    );
};
