import { useState } from 'react';
import { DataGrid } from '@mui/x-data-grid';
import { Container, TextField, Autocomplete, Button } from "@mui/material";

function ToDoList() {
    const [rows, setRows] = useState([
        { id: 1, itemName: 'Item 1', priority: 'High', status: 'Pending' },
        { id: 2, itemName: 'Item 2', priority: 'Medium', status: 'In Progress' },
        { id: 3, itemName: 'Item 3', priority: 'Low', status: 'Completed' },

    ]);

    // Define columns
    const columns = [
        { field: 'id', headerName: 'Item Number', width: 150 },
        { field: 'itemName', headerName: 'Item Name', width: 150 },
        { field: 'priority', headerName: 'Priority', width: 150 },
        { field: 'status', headerName: 'Status', width: 150 },
        {
            field: 'delete',
            headerName: 'Delete',
            sortable: false,
            width: 100,
            renderCell: (params) => (
                <Button
                    variant="outlined"
                    // onClick={() => handleDeleteRow(params.row.id)}
                >
                    Delete
                </Button>
            )
        }
    ];

    const priority = ["High", "Medium", "Low"]
    const status = ["Pending", "In Progress", "Complete"]

    const [newItem, setNewItem] = useState({
        itemName: '',
        priority: '',
        status: ''
    });

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setNewItem(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const addToList = () => {
        setRows(prevRows => [
            ...prevRows,
            {
                id: prevRows.length + 1,
                itemName: newItem.itemName,
                priority: newItem.priority,
                status: newItem.status
            }
        ]);
        setNewItem({ itemName: '', priority: '', status: '' });
    };

    return (
        <>
            <DataGrid
                rows={rows}
                columns={columns}
                pageSize={5}
                checkboxSelection
            />
            <Container maxWidth="md" style={{ textAlign: "center", display: "flex", justifyContent: "space-around" }}>
                <TextField
                    id="itemName"
                    name="itemName"
                    label="Item Name"
                    variant="outlined"
                    value={newItem.itemName}
                    onChange={handleInputChange}
                />
                <Autocomplete
                    disablePortal
                    id="combo-box-priority"
                    options={priority}
                    sx={{ width: 300 }}
                    renderInput={(params) => <TextField {...params} label="Priority" />}
                    value={newItem.priority}
                    onChange={(event, newValue) => {
                        setNewItem(prevState => ({ ...prevState, priority: newValue }));
                    }}
                />
                <Autocomplete
                    disablePortal
                    id="combo-box-status"
                    options={status}
                    sx={{ width: 300 }}
                    renderInput={(params) => <TextField {...params} label="Status" />}
                    value={newItem.status}
                    onChange={(event, newValue) => {
                        setNewItem(prevState => ({ ...prevState, status: newValue }));
                    }}
                />
                <Button variant="outlined" onClick={addToList}>Add</Button>
            </Container>
        </>
    )
}

export default ToDoList;
